{
    "model": "llama3.2:3b-instruct-fp16",
    "messages": [
        {
            "role": "user",
            "content": "Hi, can you tell me about the weather today? I'm in Maracaibo, Zulia"
        },
        {
            "role": "assistant",
            "content": "",
            "tool_calls": [
                {
                    "function": {
                        "name": "get_weather",
                        "arguments": {
                            "location": "Maracaibo, Zulia"
                        }
                    }
                }
            ]
        },
        {
            "role": "tool",
            "content": "55 degrees f, 52 aqi, sunny."
        }
    ],
    "stream": false,
    "raw": false,
    "tools": [
        {
            "type": "function",
            "function": {
                "name": "get_weather",
                "description": "get weather for a giben location",
                "parameters": {
                    "type": "object",
                    "location": {
                        "type": "string",
                        "description": "the location to get the weather, for example 'Maracaibo, Zulia', 'San Jose, CA', 'New Zealand'"
                    },
                    "required": [
                        "location"
                    ]
                }
            }
        },
        {
            "type": "function",
            "function": {
                "name": "get_running_programs",
                "description": "List the programs running on the users computer"
            }
        },
        {
            "type": "function",
            "function": {
                "name": "none",
                "description": "call this tool if you don't want to call any other tool, Pass in what you would normall state to the user and it will mirror that back out.",
                "parameters": {
                    "type": "object",
                    "input": {
                        "type": "string",
                        "description": "Text that will be mirrored out"
                    },
                    "required": [
                        "input"
                    ]
                }
            }
        }
    ],
    "options": {
        "system": "You are an AI assistant running in the cloud. You have access to some tools but it is not necessary to call them if the request can be responded to directly."
    }
}
